mapper.select:
SELECT


,meishuguan.meishuguan_uuid_number as meishuguanUuidNumber
,meishuguan.meishuguan_name as meishuguanName
,meishuguan.meishuguan_photo as meishuguanPhoto
,meishuguan.meishuguan_address as meishuguanAddress
,meishuguan.zan_number as zanNumber
,meishuguan.cai_number as caiNumber
,meishuguan.meishuguan_types as meishuguanTypes
,meishuguan.meishuguan_kucun_number as meishuguanKucunNumber
,meishuguan.meishuguan_new_money as meishuguanNewMoney
,meishuguan.meishuguan_clicknum as meishuguanClicknum
,meishuguan.jieshu_time as jieshuTime
,meishuguan.meishuguan_content as meishuguanContent
,meishuguan.meishuguan_delete as meishuguanDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney
,yonghu.yonghu_delete as yonghuDelete

FROM meishuguan_collection  a
left JOIN meishuguan meishuguan ON a.meishuguan_id = meishuguan.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.meishuguan_id = #{params.meishuguanId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.meishuguan_collection_types = #{params.meishuguanCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.meishuguan_id IS NOT NULL


        and meishuguan.meishuguan_uuid_number like CONCAT('%',#{params.meishuguanUuidNumber},'%')


        and meishuguan.meishuguan_name like CONCAT('%',#{params.meishuguanName},'%')


        and meishuguan.meishuguan_address like CONCAT('%',#{params.meishuguanAddress},'%')


          and meishuguan.zan_number >= #{params.zanNumberStart}   


          and meishuguan.zan_number <= #{params.zanNumberEnd}   


        and meishuguan.zan_number = #{params.zanNumber}


          and meishuguan.cai_number >= #{params.caiNumberStart}   


          and meishuguan.cai_number <= #{params.caiNumberEnd}   


        and meishuguan.cai_number = #{params.caiNumber}


        and meishuguan.meishuguan_types = #{params.meishuguanTypes}



          and meishuguan.meishuguan_kucun_number >= #{params.meishuguanKucunNumberStart}   


          and meishuguan.meishuguan_kucun_number <= #{params.meishuguanKucunNumberEnd}   


        and meishuguan.meishuguan_kucun_number = #{params.meishuguanKucunNumber}


          and meishuguan.meishuguan_new_money >= #{params.meishuguanNewMoneyStart}   


          and meishuguan.meishuguan_new_money <= #{params.meishuguanNewMoneyEnd}   


        and a.meishuguan_new_money = #{params.meishuguanNewMoney}


          and meishuguan.meishuguan_clicknum >= #{params.meishuguanClicknumStart}   


          and meishuguan.meishuguan_clicknum <= #{params.meishuguanClicknumEnd}   


        and meishuguan.meishuguan_clicknum = #{params.meishuguanClicknum}


          and UNIX_TIMESTAMP(meishuguan.jieshu_time) >= UNIX_TIMESTAMP(#{params.jieshuTimeStart}) 


          and UNIX_TIMESTAMP(meishuguan.jieshu_time) <= UNIX_TIMESTAMP(#{params.jieshuTimeEnd}) 


        and a.jieshu_time = #{params.jieshuTime}


        and meishuguan.meishuguan_content like CONCAT('%',#{params.meishuguanContent},'%')


          and meishuguan.meishuguan_delete >= #{params.meishuguanDeleteStart}   


          and meishuguan.meishuguan_delete <= #{params.meishuguanDeleteEnd}   


        and meishuguan.meishuguan_delete = #{params.meishuguanDelete}


          and UNIX_TIMESTAMP(meishuguan.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(meishuguan.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


        and a.new_money = #{params.newMoney}


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
